	formControl: {
		margin: theme.spacing(1),
		minWidth: 120,
		maxWidth: 300,
	},
	chips: {
		display: 'flex',
		flexWrap: 'wrap',
	},
	chip: {
		margin: 2,
	},
	noLabel: {
		marginTop: theme.spacing(3),
	},
}));

export default function Modals(props) {
	const history = useHistory();

	const classes = useStyles();
	const theme = useTheme();
	const [categoryName, setCategoryName] = React.useState();

