import React, { useEffect, useState } from 'react';
import axios from '../../../AxiosInstance';
import { useHistory } from 'react-router-dom';
import { AiOutlineSearch } from 'react-icons/ai';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';

function AddCategory(props) {
	const [categoryList, setCategoryList] = useState([]);
	const [category, setCateogry] = useState('');
	const [category2, setCateogry2] = useState('');
	const [editedCategory, setEditedCateogry] = useState('');
	const [open, setOpen] = React.useState(false);
	const [open2, setOpen2] = React.useState(false);
	const [open3, setOpen3] = React.useState(false);
	const [open4, setOpen4] = React.useState(false);
	const [dId, setDdiI] = React.useState('');
	const [msg, setMsg] = useState('');
	const [err, setError] = useState('');

	const getAllCategories = () => {
		axios
			.get('category')
			.then((res) => {
				console.log('Response', res.data);
				setCategoryList(res.data.categories);
			})
			.catch((err) => {
				console.log('Response', err.response);
			});
	};
	useEffect(() => {
		getAllCategories();
	}, []);

	const handleDelete = async (data) => {
		handleClose();
		const originalState = categoryList;

		await axios
			.delete(`category/${dId}`) //remove from Database
			.then((res) => {
				getAllCategories();
				setOpen3(false);
			})
			.catch((err) => {
				alert('ERROR : User Not Deleted');
				setCategoryList(originalState);
			});
	};

	const handleClickOpenDelete = (id) => {
		setDdiI(id);
		setOpen3(true);
	};

	const addCategoryDialogueOpen = () => {
		setOpen(true);
	};

	const handleClickOpen = (id) => {
		setDdiI(id);
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};

	const handleAddCategory = async () => {
		if (category === '') {
			return;
		}

		try {
			let insert = await axios.post('category', { name: category });
			if (insert.status === 200 || insert.status === 201) {
				setMsg('Category added Successfully');
				setOpen(false);
				setOpen2(true);
				setCateogry('');
				getAllCategories();
			}
		} catch (err) {
			setError(err.message);
			setMsg('');
			setOpen(false);
			setOpen2(true);
		}
	};

	const handleUpdateCategory = async () => {
		if (category2 === '') {
			return;
		}

		try {
			let update = await axios.patch('category/' + editedCategory._id, { name: category2 });
			if (update.status === 200 || update.status === 201) {
				setMsg('Category updated Successfully');
				setOpen4(false);
				setOpen2(true);
				setCateogry2('');
				getAllCategories();
			}
		} catch (err) {
			setError(err.message);
			setMsg('');
			setOpen4(false);
			setOpen2(true);
		}
	};

	const handleEditCategory = (category) => {
		setEditedCateogry(category);
		setCateogry2(category.name);
		setOpen4(true);
	};

	return (
		<div style={{ padding: '5rem 1rem 1rem 1rem' }}>
			<div
				style={{
					overflow: 'auto',
					backgroundColor: 'white',
					borderRadius: '6px',
				}}>
				<div className='profiles-tble-wrap'>
					<div className='search-fild-dv'>
						<div className='add-fild'>
							<button
								onClick={addCategoryDialogueOpen}
								style={{
									backgroundColor: '#ff5900',
									color: 'white',
									border: 'none',
									borderRadius: '5px',
									height: '25px',
									width: '40px',
									marginRight: '5px',
								}}>
								Add
							</button>
						</div>
					</div>
					<div className=' tablecontain'>
						<table className='adTable'>
							<tr className='adTable-tr' style={{ backgroundColor: '#1d2630' }}>
								<th className='adTable-th'>Name</th>
								<th className='adTable-th'>Action</th>
							</tr>

							{categoryList.map((item, key) => {
								return (
									<tr className='adTable-tr'>
										<td className='adTable-td'>{item.name}</td>

										<td className='adTable-td d-flex justify-center'>
											<button
												onClick={() => handleEditCategory(item)}
												style={{
													backgroundColor: '#ff5900',
													color: 'white',
													border: 'none',
													borderRadius: '5px',
													height: '25px',
													width: '40px',
													marginRight: '5px',
												}}>
												Edit
											</button>

											<button
												// onClick={() => handleDelete(item)}
												onClick={() => handleClickOpenDelete(item._id)}
												style={{
													backgroundColor: '#ff5900',
													color: 'white',
													border: 'none',
													borderRadius: '5px',
													height: '25px',
													width: '55px',
												}}>
												Delete
											</button>
										</td>
									</tr>
								);
							})}
						</table>
					</div>
				</div>
			</div>
			<Dialog open={open} onClose={handleClose} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
				<DialogTitle id='alert-dialog-title'>{'Add New Category'}</DialogTitle>
				<DialogContent>
					<DialogContentText id='alert-dialog-description'>
						<input type='text' name='category' onChange={(e) => setCateogry(e.target.value)} value={category} placeholder='Add Category' />
					</DialogContentText>
				</DialogContent>
				<DialogActions>
					<Button onClick={handleAddCategory} color='primary'>
						Add
					</Button>
					<Button onClick={handleClose} color='primary'>
						Cancel
					</Button>
				</DialogActions>
			</Dialog>

			{open2 && (
				<Dialog open={open2} onClose={() => setOpen2(false)} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
					<DialogTitle id='alert-dialog-title' style={{ backgroundColor: 'black' }}>
						<p style={{ color: 'white' }}>Reset Password</p>
					</DialogTitle>
					<DialogContent style={{ backgroundColor: 'black' }}>
						<DialogContentText id='alert-dialog-description' style={{ color: 'white' }}>
							{msg ? <p>{msg}</p> : null}
							{err ? <p>{err}</p> : null}
						</DialogContentText>
					</DialogContent>
					<DialogActions style={{ backgroundColor: 'black' }}>
						<Button onClick={() => setOpen2(false)} style={{ backgroundColor: '#ff5900', color: 'white' }}>
							Close
						</Button>
					</DialogActions>
				</Dialog>
			)}
			<Dialog open={open3} onClose={() => setOpen3(false)} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
				<DialogTitle id='alert-dialog-title' style={{ backgroundColor: 'black' }}>
					<p style={{ color: 'white' }}>Reset Password</p>
				</DialogTitle>
				<DialogContent style={{ backgroundColor: 'black', color: '#fff' }}>Are you sure to want to delete category?</DialogContent>
				<DialogActions style={{ backgroundColor: 'black' }}>
					<Button onClick={() => handleDelete()} style={{ backgroundColor: '#ff5900', color: 'white' }}>
						Delete
					</Button>
					<Button onClick={() => setOpen3(false)} style={{ backgroundColor: '#ff5900', color: 'white' }}>
						Close
					</Button>
				</DialogActions>
			</Dialog>

			<Dialog open={open4} onClose={() => setOpen4(false)} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
				<DialogTitle id='alert-dialog-title'>{'Add New Category'}</DialogTitle>
				<DialogContent>
					<DialogContentText id='alert-dialog-description'>
						<input type='text' name='category2' onChange={(e) => setCateogry2(e.target.value)} value={category2} placeholder='Update Category' />
					</DialogContentText>
				</DialogContent>
				<DialogActions>
					<Button onClick={handleUpdateCategory} color='primary'>
						Update
					</Button>
					<Button onClick={() => setOpen4(false)} color='primary'>
						Cancel
					</Button>
				</DialogActions>
			</Dialog>
		</div>
	);
}

export default AddCategory;
