import React, { useState, useEffect } from 'react';
import axios from '../../../AxiosInstance';
import { Link } from 'react-router-dom';
import { AiOutlineSearch } from 'react-icons/ai';
import './Profile.css';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import Button from '@material-ui/core/Button';
import DialogTitle from '@material-ui/core/DialogTitle';
export default function Profile() {
	const [userList, setUserList] = useState([]);
	const [searchItem, setSearchItem] = useState([]);
	const [selectedUser, setSelectedUser] = useState({});
	const [open, setOpen] = useState(false);
	const [open1, setOpen1] = useState(false);
	const [msg, setMsg] = useState('');
	const [err, setError] = useState('');

	const handleSearch = (e) => {
		setSearchItem(e.target.value);
		console.log(('SearchItem:', searchItem));
	};

	const getUsers = () => {
		axios
			.get('auth/getAllUsers')
			.then((res) => {
				console.log('Response', res.data);
				setUserList(res.data.users.filter((user) => !user.invited));
			})
			.catch((err) => {
				console.log('Response', err.response);
			});
	};
	useEffect(() => {
		console.log('AAAAAAAA');
		getUsers();
	}, []);

	const cancelSubscription = async (data) => {
		await axios
			.post('admin/cancelSubscription', {
				userId: data._id,
			})
			.then((res) => {
				getUsers();
			})
			.catch((err) => {
				alert('ERROR : Subscription not canceled');
			});
	};

	const deleteAndRefund = async (data) => {
		const originalState = userList;
		const temp = userList.filter((c) => c._id !== data._id);
		setUserList(temp);

		await axios
			.post('admin/deleteAndRefund', {
				userId: data._id,
			})
			.then((res) => {})
			.catch((err) => {
				alert('ERROR : User Not Deleted');
				setUserList(originalState);
			});
	};

	const cancelRefundSubscription = async (type) => {
		let data = {
			userId: selectedUser._id,
			type: type,
		};

		await axios
			.post('admin/cancelRefundSubscription', data)
			.then((res) => {
				getUsers();
				setError('');
				setOpen1(true);
				setOpen(false);
				setMsg('Subscription Cancel Successfully');
			})
			.catch((err) => {
				// alert('ERROR : User Not Deleted');
				setOpen1(true);
				setMsg('');
				if ((err.response && err.response.message) || (err.response && err.response.error)) {
					setError(err.response.message);
				} else {
					setError(err.message);
				}
			});
	};

	return (
		<div style={{ padding: '5rem 1rem 1rem 1rem' }}>
			<Dialog open={open} onClose={() => setOpen(false)} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
				<DialogTitle id='alert-dialog-title' style={{ backgroundColor: 'black' }}>
					<p style={{ color: 'white' }}>Cancel/Refund Subscription</p>
				</DialogTitle>
				<DialogContent style={{ backgroundColor: 'black' }}>
					<p style={{ color: '#fff' }}>Are you sure to Cancel/Refund subscription? </p>
					<p style={{ color: '#fff' }}>Note : After Cancel/Refund user will bhe deleted from database.</p>
					<div style={{ margin: '1rem 0' }}>
						<Button onClick={() => cancelRefundSubscription('cancel')} style={{ backgroundColor: '#ff5900', color: 'white' }}>
							Cancel Subscription
						</Button>
					</div>
					<div>
						<Button onClick={() => cancelRefundSubscription('refund')} style={{ backgroundColor: '#ff5900', color: 'white' }}>
							Cancel Subscription And Refund
						</Button>
					</div>
				</DialogContent>
				<DialogActions style={{ backgroundColor: 'black' }}>
					<Button onClick={() => setOpen(false)} style={{ backgroundColor: '#ff5900', color: 'white' }}>
						Close
					</Button>
				</DialogActions>
			</Dialog>

			<Dialog open={open1} onClose={() => setOpen1(false)} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
				<DialogTitle id='alert-dialog-title' style={{ backgroundColor: 'black' }}>
					<p style={{ color: 'white' }}>Response</p>
				</DialogTitle>
				<DialogContent style={{ backgroundColor: 'black' }}>
					<DialogContentText id='alert-dialog-description' style={{ color: 'white' }}>
						{msg ? <p>{msg}</p> : null}
						{err ? <p>{err}</p> : null}
					</DialogContentText>
				</DialogContent>
				<DialogActions style={{ backgroundColor: 'black' }}>
					<Button onClick={() => setOpen1(false)} style={{ backgroundColor: '#ff5900', color: 'white' }}>
						Close
					</Button>
				</DialogActions>
			</Dialog>
			<div
				style={{
					overflow: 'auto',
					backgroundColor: 'white',
					borderRadius: '6px',
				}}>
				<div className='profiles-tble-wrap'>
					<div className='search-fild-dv'>
						<div className='search-fild'>
							<AiOutlineSearch style={{ fontSize: '22px', color: '#afabab' }} />
							<input type='text' placeholder='Search...' name='searchprofile' onChange={handleSearch} className='profile-search-inpt' />
						</div>
					</div>
					<div className=' tablecontain'>
						<table className='adTable'>
							<tr className='adTable-tr' style={{ backgroundColor: '#1d2630' }}>
								<th className='adTable-th'>Name</th>
								<th className='adTable-th'>Last Name</th>
								<th className='adTable-th'>Email</th>
								<th className='adTable-th'>Models Downloaded</th>
								<th className='adTable-th'>Subscription Status</th>
								<th className='adTable-th'>Starting Date</th>
								<th className='adTable-th'></th>
							</tr>

							{userList
								.filter((val) => {
									if (searchItem == '') {
										return val;
									} else if (val.firstName.toLowerCase().includes(searchItem.toLowerCase())) {
										return val;
										// } else if (
										//   val.lastName
										//     .toLowerCase()
										//     .includes(searchItem.toLowerCase())
										// ) {
										//   return val
									} else if (val.email.toLowerCase().includes(searchItem.toLowerCase())) {
										return val;
									}
								})
								.map((item, key) => {
									return (
										<tr className='adTable-tr'>
											<td className='adTable-td'>{item.firstName}</td>
											<td className='adTable-td'>{item.lastName}</td>
											<td className='adTable-td'>{item.email}</td>
											<td className='adTable-td'>{item.modelDownloaded ? item.modelDownloaded.length : 0}</td>
											<td className='adTable-td'>
												{item.payment && <div className={item.payment.status == 'active' ? 'adTable-tdG1' : item.payment.status == 'Cancelled' ? 'adTable-tdY' : item.payment.status == 'Refunded' ? 'adTable-tdR1' : ' adTable-tdP'}>{item.payment.status}</div>}
											</td>
											<td className='adTable-td'>{item.startedAt}</td>

											<td className='adTable-td'>
												<div>
													<Link
														to={{
															pathname: '/customerpanel',
															state: { id: item._id },
														}}>
														<button
															style={{
																marginRight: '0.5rem',
																color: 'white',
																backgroundColor: '#ff5900',
																border: 'none',
																height: '25px',
																borderRadius: '5px',
															}}>
															Edit
														</button>
													</Link>

													<button
														onClick={() => {
															// cancelSubscription(item);
															setSelectedUser(item);
															setOpen(true);
														}}
														disabled={item.status === 'canceled'}
														style={{
															marginRight: '0.5rem',
															color: 'white',
															backgroundColor: '#ff5900',
															border: 'none',
															height: '25px',
															borderRadius: '5px',
														}}>
														Cancel
													</button>

													{/* <button
														onClick={() => {
															deleteAndRefund(item);
														}}
														style={{
															color: 'white',
															backgroundColor: '#ff5900',
															border: 'none',
															height: '25px',
															borderRadius: '5px',
														}}>
														Refund
													</button> */}
												</div>
											</td>
										</tr>
									);
								})}
						</table>
					</div>
				</div>
			</div>
		</div>
	);
}
