import React, { useState, useEffect } from 'react';
import axios from '../../../AxiosInstance';
import { Link } from 'react-router-dom';
import { AiOutlineSearch } from 'react-icons/ai';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import './invitation.css';

function Invitation() {
	const [userList, setUserList] = useState([]);
	const [searchItem, setSearchItem] = useState([]);
	const [open, setOpen] = useState(false);
	const [open2, setOpen2] = useState(false);
	const [msg, setMsg] = useState('');
	const [err, setError] = useState('');

	const [values, setValues] = useState({
		email: '',
	});
	const handleChange = (e) => {
		const { name, value } = e.target;
		setValues((prevValues) => {
			return {
				...prevValues,
				[name]: value,
			};
		});
	};
	const handleSearch = (e) => {
		setSearchItem(e.target.value);
		console.log(('SearchItem:', searchItem));
	};

	const handleClickOpen = () => {
		setOpen(true);
	};
	const handleClose = () => {
		setOpen(false);
	};

	const handleSendInvitation = () => {
		axios
			.post('auth/inviteUser', { email: values.email })
			.then((res) => {
				setOpen2(true);
				setOpen(false);
				setMsg(res.data.message);
			})
			.catch((err) => {
				setOpen2(true);
				if (err.data) {
					setMsg(err.data.error);
				} else {
					setMsg(err.message);
				}
				console.log('Response', err.response);
			});
	};
	const getUsers = () => {
		axios
			.get('auth/getAllUsers')
			.then((res) => {
				console.log('Response', res.data);
				setUserList(res.data.users.filter((u) => u.invited === true));
			})
			.catch((err) => {
				console.log('Response', err.response);
			});
	};
	useEffect(() => {
		getUsers();
	}, []);

	const deleteAndRefund = async (data) => {
		const originalState = userList;
		const temp = userList.filter((c) => c._id !== data._id);
		setUserList(temp);

		await axios
			.post('admin/deleteAndRefund', {
				userId: data._id,
			})
			.then((res) => {})
			.catch((err) => {
				alert('ERROR : User Not Deleted');
				setUserList(originalState);
			});
	};

	const changeUserStatus = async (data, status) => {
		try {
			let changeStatus = await axios.post('auth/activateUser', {
				id: data._id,
				status: status,
			});
			if (changeStatus.status === 200 || changeStatus.status === 201) {
				alert(changeStatus.data.message);
				getUsers();
			}
		} catch (err) {
			alert('ERROR : User not ' + status);
		}
	};

	const deleteUser = async (data) => {
		try {
			let changeStatus = await axios.post('auth/deleteUser', {
				id: data._id,
			});
			if (changeStatus.status === 200 || changeStatus.status === 201) {
				alert(changeStatus.data.message);
				getUsers();
			}
		} catch (err) {
			alert('ERROR : User not deleted');
		}
	};

	return (
		<div style={{ padding: '5rem 1rem 1rem 1rem' }}>
			<Dialog open={open2} onClose={() => setOpen2(false)} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
				{/* <DialogTitle
                  id="alert-dialog-title"
                  style={{ backgroundColor: 'black' }}
                >
                  <p style={{ color: 'white' }}>Reset Password</p>
                </DialogTitle> */}
				<DialogContent style={{ backgroundColor: 'black' }}>
					<DialogContentText id='alert-dialog-description' style={{ color: 'white' }}>
						{msg ? <p>{msg}</p> : null}
						{err ? <p>{err}</p> : null}
					</DialogContentText>
				</DialogContent>
				<DialogActions style={{ backgroundColor: 'black' }}>
					<button
						onClick={() => setOpen2(false)}
						style={{
							marginRight: '0.5rem',
							color: 'white',
							backgroundColor: '#ff5900',
							border: 'none',
							height: '25px',
							borderRadius: '5px',
						}}>
						Close
					</button>
				</DialogActions>
			</Dialog>
			<div
				style={{
					overflow: 'auto',
					backgroundColor: 'white',
					borderRadius: '6px',
				}}>
				<div className='profiles-tble-wrap'>
					<div className='search-fild-dv'>
						<div className='invitation-button-wrapper'>
							<button
								onClick={handleClickOpen}
								style={{
									marginRight: '0.5rem',
									color: 'white',
									backgroundColor: '#ff5900',
									border: 'none',
									height: '25px',
									borderRadius: '5px',
								}}>
								Invite User
							</button>
							<Dialog open={open} onClose={handleClose} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
								<DialogContent style={{ backgroundColor: 'black' }}>
									<DialogContentText id='alert-dialog-description'>
										<div style={{ width: '100%' }}>
											<div className='row'>
												<div className='col-lg-12'>
													<div style={{ display: 'flex', justifyContent: 'center' }}>
														<div className=''>
															<div style={{ marginBottom: '.5rem' }} className=''>
																<input value={values.email} name='email' onChange={handleChange} v type='email' placeholder='User Email' required className='modl-inpt-fild' />
															</div>
														</div>
													</div>
												</div>
											</div>
										</div>
									</DialogContentText>
								</DialogContent>
								<DialogActions style={{ backgroundColor: 'black' }}>
									<button
										onClick={handleSendInvitation}
										style={{
											marginRight: '0.5rem',
											color: 'white',
											backgroundColor: '#ff5900',
											border: 'none',
											height: '25px',
											borderRadius: '5px',
										}}>
										Invite
									</button>
									<button
										onClick={handleClose}
										style={{
											marginRight: '0.5rem',
											color: 'white',
											backgroundColor: '#ff5900',
											border: 'none',
											height: '25px',
											borderRadius: '5px',
										}}>
										Cancel
									</button>
								</DialogActions>
							</Dialog>
						</div>
						<div className='search-fild'>
							<AiOutlineSearch style={{ fontSize: '22px', color: '#afabab' }} />
							<input type='text' placeholder='Search...' name='searchprofile' onChange={handleSearch} className='profile-search-inpt' />
						</div>
					</div>
					<div className=' tablecontain'>
						<table className='adTable'>
							<tr className='adTable-tr' style={{ backgroundColor: '#1d2630' }}>
								<th className='adTable-th'>Name</th>
								<th className='adTable-th'>Last Name</th>
								<th className='adTable-th'>Email</th>
								<th className='adTable-th'>Models Downloaded</th>
								{/* <th className='adTable-th'>Subscription Status</th> */}
								<th className='adTable-th'>User Status</th>
								<th className='adTable-th'>Starting Date</th>
								<th className='adTable-th'></th>
							</tr>

							{userList
								.filter((val) => {
									if (searchItem == '') {
										return val;
									} else if (val.firstName.toLowerCase().includes(searchItem.toLowerCase())) {
										return val;
										// } else if (
										//   val.lastName
										//     .toLowerCase()
										//     .includes(searchItem.toLowerCase())
										// ) {
										//   return val
									} else if (val.email.toLowerCase().includes(searchItem.toLowerCase())) {
										return val;
									}
								})
								.map((item, key) => {
									return (
										<tr className='adTable-tr'>
											<td className='adTable-td'>{item.firstName}</td>
											<td className='adTable-td'>{item.lastName}</td>
											<td className='adTable-td'>{item.email}</td>
											<td className='adTable-td'>{item.modelDownloaded ? item.modelDownloaded.length : 0}</td>
											{/* <td className='adTable-td'>
												{item.payment && <div className={item.payment.status == 'active' ? 'adTable-tdG1' : item.payment.status == 'Cancelled' ? 'adTable-tdY' : item.payment.status == 'Refunded' ? 'adTable-tdR1' : ' adTable-tdP'}>{item.payment.status}</div>}
											</td> */}
											<td className='adTable-td'>{item.status}</td>
											<td className='adTable-td'>{item.startedAt}</td>

											<td className='adTable-td'>
												<div>
													<Link
														to={{
															pathname: '/customerpanel',
															state: { id: item._id },
														}}>
														<button
															style={{
																marginRight: '0.5rem',
																color: 'white',
																backgroundColor: '#ff5900',
																border: 'none',
																height: '25px',
																borderRadius: '5px',
															}}>
															Edit
														</button>
													</Link>

													{item.status === 'registered' || item.status === 'freeze' ? (
														<button
															onClick={() => {
																changeUserStatus(item, 'active');
															}}
															style={{
																marginRight: '0.5rem',
																color: 'white',
																backgroundColor: '#ff5900',
																border: 'none',
																height: '25px',
																borderRadius: '5px',
															}}>
															Activate
														</button>
													) : (
														<button
															onClick={() => {
																changeUserStatus(item, 'freeze');
															}}
															style={{
																marginRight: '0.5rem',
																color: 'white',
																backgroundColor: '#ff5900',
																border: 'none',
																height: '25px',
																borderRadius: '5px',
															}}>
															Freeze
														</button>
													)}

													<button
														onClick={() => {
															deleteUser(item);
														}}
														style={{
															color: 'white',
															backgroundColor: '#ff5900',
															border: 'none',
															height: '25px',
															borderRadius: '5px',
														}}>
														Delete
													</button>
												</div>
											</td>
										</tr>
									);
								})}
						</table>
					</div>
				</div>
			</div>
		</div>
	);
}

export default Invitation;
