import React, { useEffect, useState } from 'react'
import "./ham.scss"
const Hamm = () => {
    const [timer, setTimer] = useState(20)


    useEffect(() => {
        setInterval(
            () =>
                setTimer((timer) => {
                    if (timer > 0) {
                        return timer - 1;
                    } else {
                        return 0;
                    }
                }),
            1000
        );
    }, [])
    return (
        <>
            <div style={{ backgroundColor: (timer <= 15 && timer > 10) ? "blue" : (timer < 10 || timer == 5) ? "grey" : timer == 5 ? "yellow" : null }} className="hamm__contianer">

                <div className="hamm__header">
                    <h3>Timer</h3>
                    <p className="hamm__para">
                        {timer}

                    </p>
                </div>
            </div>

        </>
    )
}

export default Hamm
