import React, { useState, useEffect } from 'react';
import './Drawer.css';
import { useHistory } from 'react-router-dom';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Divider from '@material-ui/core/Divider';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import IconButton from '@material-ui/core/IconButton';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import MenuIcon from '@material-ui/icons/Menu';
import Button from '@material-ui/core/Button';
import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Dashboard from './components/Dashboard';
import Modals from './components/Modals';
import Profile from './components/Profile';
import AllModels from './components/AllModels';
import AddCategory from './components/AddCategory';
import EmialMarketing from './components/EmialMarketing';
import Invitation from './components/Invitation';
import { FaModx, FaUsers } from 'react-icons/fa';
import { CgProfile } from 'react-icons/cg';
import { BiCollection } from 'react-icons/bi';
import { SiMarketo } from 'react-icons/si';
// import logopic from '../../Assets/images/logo/logow.png';
import { Link } from 'react-router-dom';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import axios from '../../AxiosInstance';

const drawerWidth = 280;

const useStyles = makeStyles((theme) => ({
	root: {
		display: 'flex',
	},
	drawer: {
		[theme.breakpoints.up('sm')]: {
			width: drawerWidth,
			flexShrink: 0,
		},
	},
	appBar: {
		[theme.breakpoints.up('sm')]: {
			width: `calc(100% - ${drawerWidth}px)`,
			marginLeft: drawerWidth,
		},
	},
	menuButton: {
		marginRight: theme.spacing(2),
		[theme.breakpoints.up('sm')]: {
			display: 'none',
		},
	},
	// necessary for content to be below app bar
	toolbar: theme.mixins.toolbar,
	drawerPaper: {
		width: drawerWidth,
	},
	content: {
		flexGrow: 1,
		padding: theme.spacing(3),
	},
}));

function ResponsiveDrawer(props) {
	const history = useHistory();

	const { window } = props;

	const [isDashboard, setIsDashboard] = useState(false);
	const [isModals, setIsModals] = useState(true);
	const [isProfile, setIsProfile] = useState(false);
	const [isAllModels, setIsAllModels] = useState(false);
	const [isAllCategory, setIsAllCategory] = useState(false);
	const [isInvitation, setIsInvitation] = useState(false);

	const [isEmialMarketing, setIsEmialMarketing] = useState(false);

	const [open, setOpen] = React.useState(false);
	const handleClickOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};

	// useEffect(() => {
	// 	console.log('History', history.location.state);
	// 	let token = localStorage.getItem('adminToken');
	// 	if (!token) {
	// 		history.push('/adminlogin');
	// 	}
	// 	if (history.location.state) {
	// 		setIsModals(true);
	// 		setIsAllModels(true);
	// 	}
	// }, []);

	const handleLogout = () => {
		localStorage.removeItem('adminToken');
		history.push('/adminlogin');
	};

	const classes = useStyles();
	const theme = useTheme();
	const [mobileOpen, setMobileOpen] = React.useState(false);

	const handleDrawerToggle = () => {
		setMobileOpen(!mobileOpen);
	};

	const [values, setValues] = useState({
		password: '',
		oldPassword: '',
	});

	const handleChange = (event) => {
		setValues({
			...values,
			[event.target.name]: event.target.value,
		});
	};

	const [isEdited, setIsEdited] = React.useState(false);
	const [editedModel, setEditedModel] = React.useState({});

	const handleEditModel = (data) => {
		setIsEdited(true);
		setEditedModel(data);
		setIsDashboard(false);
		setIsModals(true);
		setIsProfile(false);
		setIsAllModels(false);
		setIsEmialMarketing(false);
		setMobileOpen(false);
		setIsAllCategory(false);
		setIsInvitation(false);
	};

	const handleChangePassword = async (e) => {
		e.preventDefault();

		try {
			axios.defaults.headers.authorization = localStorage.getItem('adminToken');
			let changePasswod = await axios.post('/admin/changePassword', values);
			if (changePasswod.status === 200) {
				setValues({
					oldPassword: '',
					password: '',
				});
				setOpen(false);
			}
		} catch (err) {
			console.log(err);
		}
	};
	const drawer = (
		<div style={{ backgroundColor: '#25303c', height: '100vh' }}>
			<div style={{ padding: '60px 0' }}>
				<Link to='/'>
					<img src={logopic} alt='' style={{ objectFit: 'contain', width: '230px' }} />
				</Link>
			</div>
			<Divider />
			<List>
				<ListItem
					button
					style={
						isModals
							? {
									backgroundColor: '#2e2d2d',
									borderRadius: '10px',
									width: '250px',
									marginLeft: '0.8rem',
									color: 'white',
							  }
							: { width: '250px', marginLeft: '0.8rem', color: 'white' }
					}
					onClick={() => {
						setIsEdited(false);
						setIsDashboard(false);
						setIsModals(true);
						setIsProfile(false);
						setIsAllModels(false);
						setIsEmialMarketing(false);
						setMobileOpen(false);
						setEditedModel({});
						setIsAllCategory(false);
						setIsInvitation(false);
					}}>
					<ListItemIcon style={{ minWidth: '38px' }}>
						<FaModx
							style={
								isModals
									? {
											fontSize: '20px',
											color: 'white',
									  }
									: { color: 'white' }
							}
						/>
					</ListItemIcon>
					<ListItemText primary='Add Modal' />
				</ListItem>

				<ListItem
					button
					style={
						isAllCategory
							? {
									backgroundColor: '#2e2d2d',
									borderRadius: '10px',
									width: '250px',
									marginLeft: '0.8rem',
									color: 'white',
							  }
							: { width: '250px', marginLeft: '0.8rem', color: 'white' }
					}
					onClick={() => {
						setIsEdited(false);
						setIsDashboard(false);
						setIsModals(false);
						setIsProfile(false);
						setIsAllModels(false);
						setIsEmialMarketing(false);
						setMobileOpen(false);
						setEditedModel({});
						setIsAllCategory(true);
						setIsInvitation(false);
					}}>
					<ListItemIcon style={{ minWidth: '38px' }}>
						<FaModx
							style={
								isAllCategory
									? {
											fontSize: '20px',
											color: 'white',
									  }
									: { color: 'white' }
							}
						/>
					</ListItemIcon>
					<ListItemText primary='Add Category' />
				</ListItem>

				<ListItem
					button
					style={
						isProfile
							? {
									backgroundColor: '#2e2d2d',
									borderRadius: '10px',
									width: '250px',
									marginLeft: '0.8rem',
									color: 'white',
							  }
							: { width: '250px', marginLeft: '0.8rem', color: 'white' }
					}
					onClick={() => {
						setEditedModel({});
						setIsEdited(false);
						setIsDashboard(false);
						setIsModals(false);
						setIsProfile(true);
						setIsAllModels(false);
						setIsEmialMarketing(false);
						setMobileOpen(false);
						setIsAllCategory(false);
						setIsInvitation(false);
					}}>
					<ListItemIcon style={{ minWidth: '38px' }}>
						<CgProfile
							style={
								isProfile
									? {
											fontSize: '20px',
											color: 'white',
									  }
									: { color: 'white' }
							}
						/>
					</ListItemIcon>
					<ListItemText primary='Profiles' />
				</ListItem>

				<ListItem
					button
					style={
						isInvitation
							? {
									backgroundColor: '#2e2d2d',
									borderRadius: '10px',
									width: '250px',
									marginLeft: '0.8rem',
									color: 'white',
							  }
							: { width: '250px', marginLeft: '0.8rem', color: 'white' }
					}
					onClick={() => {
						setEditedModel({});
						setIsEdited(false);
						setIsDashboard(false);
						setIsModals(false);
						setIsProfile(false);
						setIsAllModels(false);
						setIsEmialMarketing(false);
						setMobileOpen(false);
						setIsAllCategory(false);
						setIsInvitation(true);
					}}>
					<ListItemIcon style={{ minWidth: '38px' }}>
						<FaUsers
							style={
								isInvitation
									? {
											fontSize: '20px',
											color: 'white',
									  }
									: { color: 'white' }
							}
						/>
					</ListItemIcon>
					<ListItemText primary='Invitations' />
				</ListItem>

				<ListItem
					button
					style={
						isAllModels
							? {
									backgroundColor: '#2e2d2d',
									borderRadius: '10px',
									width: '250px',
									marginLeft: '0.8rem',
									color: 'white',
							  }
							: { width: '250px', marginLeft: '0.8rem', color: 'white' }
					}
					onClick={() => {
						setIsEdited(false);
						setIsDashboard(false);
						setIsModals(false);
						setIsProfile(false);
						setIsAllModels(true);
						setIsEmialMarketing(false);
						setEditedModel({});
						setMobileOpen(false);
						setIsAllCategory(false);
						setIsInvitation(false);
					}}>
					<ListItemIcon style={{ minWidth: '38px' }}>
						<BiCollection
							style={
								isAllModels
									? {
											fontSize: '20px',
											color: 'white',
									  }
									: { color: 'white' }
							}
						/>
					</ListItemIcon>
					<ListItemText primary='All Models' />
				</ListItem>

				<ListItem
					button
					style={
						isEmialMarketing
							? {
									backgroundColor: '#2e2d2d',
									borderRadius: '10px',
									width: '250px',
									marginLeft: '0.8rem',
									color: 'white',
							  }
							: { width: '250px', marginLeft: '0.8rem', color: 'white' }
					}
					onClick={() => {
						setIsEdited(false);
						setIsDashboard(false);
						setIsModals(false);
						setIsProfile(false);
						setIsAllModels(false);
						setIsEmialMarketing(true);
						setMobileOpen(false);
						setEditedModel({});
						setIsAllCategory(false);
						setIsInvitation(false);
					}}>
					<ListItemIcon style={{ minWidth: '38px' }}>
						<SiMarketo
							style={
								isEmialMarketing
									? {
											fontSize: '20px',
											color: 'white',
									  }
									: { color: 'white' }
							}
						/>
					</ListItemIcon>
					<ListItemText primary='Emial Marketing' />
				</ListItem>
			</List>
			<Divider />
		</div>
	);

	const container = window !== undefined ? () => window().document.body : undefined;

	return (
		<div className={classes.root}>
			<CssBaseline />
			<AppBar
				position='fixed'
				className={classes.appBar}
				style={{
					background: '#25303c',
					boxShadow: 'none',
					justifyContent: 'flex-end',
				}}>
				<Toolbar>
					<IconButton color='inherit' aria-label='open drawer' edge='start' onClick={handleDrawerToggle} className={classes.menuButton} style={{ color: 'black' }}>
						<MenuIcon />
					</IconButton>
					<div
						style={{
							display: 'flex',
							justifyContent: 'flex-end',
							width: '100%',
						}}>
						<Button
							onClick={handleClickOpen}
							style={{
								backgroundColor: '#ff5700',
								color: 'white',
								fontWeight: '600',
								marginRight: '.5rem',
							}}
							color='inherit'>
							Change Password
						</Button>
						<Button
							onClick={handleLogout}
							style={{
								backgroundColor: '#ff5700',
								color: 'white',
								fontWeight: '600',
							}}
							color='inherit'>
							Logout
						</Button>
					</div>
				</Toolbar>
			</AppBar>
			<nav className={classes.drawer} aria-label='mailbox folders'>
				{/* The implementation can be swapped with js to avoid SEO duplication of links. */}
				<Hidden smUp implementation='css'>
					<Drawer
						container={container}
						variant='temporary'
						anchor={theme.direction === 'rtl' ? 'right' : 'left'}
						open={mobileOpen}
						onClose={handleDrawerToggle}
						classes={{
							paper: classes.drawerPaper,
						}}
						ModalProps={{
							keepMounted: true, // Better open performance on mobile.
						}}>
						{drawer}
					</Drawer>
				</Hidden>
				<Hidden xsDown implementation='css'>
					<Drawer
						classes={{
							paper: classes.drawerPaper,
						}}
						variant='permanent'
						open>
						{drawer}
					</Drawer>
				</Hidden>
			</nav>
			<main
				style={{
					width: '100%',
					height: '100vh',
					backgroundColor: '#25303c',
					overflow: 'auto',
				}}>
				{/* <div className={classes.toolbar} /> */}

				{isDashboard && <Dashboard />}
				{isModals && <Modals isEdited={isEdited} editedModel={editedModel} />}
				{isProfile && <Profile />}
				{isAllModels && <AllModels handleEditModel={handleEditModel} />}
				{isEmialMarketing && <EmialMarketing />}
				{isAllCategory && <AddCategory />}
				{isInvitation && <Invitation />}

				<Dialog open={open} onClose={handleClose} aria-labelledby='alert-dialog-title' aria-describedby='alert-dialog-description'>
					<DialogTitle id='alert-dialog-title'>{'Change Admin Password'}</DialogTitle>
					<DialogContent>
						<DialogContentText id='alert-dialog-description'>
							<form onSubmit={handleChangePassword} className=''>
								<div style={{ width: '100%' }}>
									<div className='row'>
										<div className='col-lg-12'>
											<div style={{ display: 'flex', justifyContent: 'center' }}>
												<div className=''>
													<div style={{ marginBottom: '.5rem' }} className=''>
														<input value={values.oldPassword} name='oldPassword' onChange={handleChange} v type='password' placeholder='Old Password' required className='modl-inpt-fild' />
													</div>
													<div style={{ marginBottom: '.5rem' }} className=''>
														<input required value={values.password} name='password' onChange={handleChange} type='password' placeholder='New Password' className='modl-inpt-fild' />
													</div>
													<Button variant='contained' type='submit' className='uplod-model-bttn' color='primary'>
														Submit
													</Button>
												</div>
											</div>
										</div>
									</div>
								</div>
							</form>
						</DialogContentText>
					</DialogContent>
					<DialogActions>
						<Button onClick={handleClose} color='primary'>
							Cancel
						</Button>
					</DialogActions>
				</Dialog>
			</main>
		</div>
	);
}

ResponsiveDrawer.propTypes = {
	/**
	 * Injected by the documentation to work in an iframe.
	 * You won't need it on your project.
	 */
	window: PropTypes.func,
};

export default ResponsiveDrawer;
