import "./Cart.scss";
import img1 from "./s1.jpg";
import img2 from "./s3.jpg";
import img3 from "./s6.jpg";
import img4 from "./s10.jpg";

import AddCircleIcon from "@material-ui/icons/AddCircle";
import RemoveCircleIcon from "@material-ui/icons/RemoveCircle";
// import {} from "@material-ui/core/stac";
import { Button } from "@material-ui/core";

import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.white,

    fontWeight: "bold",
    color: theme.palette.common.black,
  },
  body: {
    fontSize: 16,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      //   backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const rows = [
  {
    name: "T-Shirt",
    price: "$255.00",
    quantity: "7",
    imgsrc: img1,
    total: "$510",
    plusicon: <AddCircleIcon />,
    minusicon: <RemoveCircleIcon />,
  },
  {
    name: "Branded Watch",
    price: "$525.00",
    quantity: "3",
    imgsrc: img2,
    total: "$810",
    plusicon: <AddCircleIcon />,
    minusicon: <RemoveCircleIcon />,
  },
  {
    name: "Nike Shoes",
    price: "$125.00",
    quantity: "5",
    imgsrc: img3,
    total: "$610",
    plusicon: <AddCircleIcon />,
    minusicon: <RemoveCircleIcon />,
  },
  {
    name: "Sunglasses",
    price: "$100.00",
    quantity: "5",
    imgsrc: img4,
    total: "$500",
    plusicon: <AddCircleIcon />,
    minusicon: <RemoveCircleIcon />,
  },
];

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
});

const Cart = () => {
  const classes = useStyles();
  return (
    <>
      <div className="main_container">
        <div className="cart_container">
          <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="customized table">
              <TableHead>
                <TableRow>
                  <StyledTableCell  style={{ width: "350px" }} >Products</StyledTableCell>
                  <StyledTableCell align="right">Price</StyledTableCell>
                  <StyledTableCell align="right">Qty</StyledTableCell>
                  <StyledTableCell align="right">Total</StyledTableCell>
                  {/* <StyledTableCell align="right">Protein&nbsp;(g)</StyledTableCell> */}
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row, index) => (
                  <StyledTableRow key={index}>
                    <StyledTableCell
                      style={{ display: "flex", alignItems: "center" }}
                      component="th"
                      scope="row"
                    >
                      <img className="tilte_img" src={row.imgsrc} alt="" />
                      {row.name}
                    </StyledTableCell>
                    {/* <StyledTableCell align="right"></StyledTableCell> */}
                    <StyledTableCell align="right">{row.price}</StyledTableCell>
                    <StyledTableCell
                      align="right"
                      style={{ fontSize: "1.8rem" }}
                    >
                      <div className="quantiy">
                        <span className="minus">{row.minusicon}</span>
                        <span className="quant">{row.quantity}</span>
                        <span className="plus"> {row.plusicon}</span>
                      </div>
                    </StyledTableCell>
                    <StyledTableCell
                      style={{ fontWeight: "bold" }}
                      align="right"
                    >
                      {row.total}
                    </StyledTableCell>
                  </StyledTableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
        <div className="buttons">
            <div className="button_1">
        <Button variant="contained" color="primary">
          Shop More
        </Button>
        </div>
        <Button variant="contained">Update Cart</Button>
        </div>
        <div className="snd_box">
        <TableContainer  style={{ width: "550px" }}  component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
        </div>
      </div>
    </>
  );
};

export default Cart;
